pragma solidity ^0.4.23;

import "./BoostableMineableToken.sol";
import "./Antigasable.sol";

contract MineableToken is BoostableMineableToken, Antigasable {

    using SafeMath for uint;

    address public mithrilTokenAddress;

    address public mithrilQuarryAddress;

    // ------------------------------------------------------------------------
    // Constructor
    // ------------------------------------------------------------------------
    constructor(address _mithrilTokenAddress, address _mithrilQuarryAddress, address _boosterAddress, 
        string _symbol, string _name, uint8 _decimals, uint _supply, uint _reward, uint _adjustmentBlockCount, uint _initialDifficulty) 
        BoostableMineableToken(_boosterAddress)
        ERC20Mineable(_symbol, _name, _decimals, _supply, _reward, _adjustmentBlockCount, _initialDifficulty)
        public
    {
        symbol = _symbol;
        name = _name;
        mithrilTokenAddress = _mithrilTokenAddress;
        mithrilQuarryAddress = _mithrilQuarryAddress;
    }

    function _reward() internal returns (uint amount) {
        amount = super._reward();
        rewardAntigas(mithrilTokenAddress, mithrilQuarryAddress);
    }

}
